mapper.select:
SELECT 
FROM job_info_link AS t
WHERE t.id = #{id}

SELECT 
FROM job_info_link AS t
WHERE t.infoId = #{infoId} and  jobInfoId=#{jobInfoId}

mapper.update:
UPDATE job_info_link
SET
	child_jobid = #{childJobId},
	parent_jobid=#{parentJobId},
	trigger_status=#{triggerStatus}
WHERE id = #{id} and jobInfoId=#{jobInfoId} and infoId=#{infoId}

mapper.select:
SELECT 
FROM job_info_link AS t
where t.parent_jobid='' and  t.jobInfoId=#{jobInfoId}
