mapper.select:
select a.id,a.name,a.code,a.description,a.type,a.personalise_type,a.relation,a.regular from universal_rule as a where a.is_delete = 0
and a.name like CONCAT(CONCAT('%', #{name}), '%')
order by a.create_time desc limit #{offset}, #{pageSize}

select id,name from universal_rule
where is_delete = 0
and type = #{type}
